floor_array = floor_parens.split('')
def floor(floor_string)
  floor = 0
  position = 0



    while position >= 0
      floor_string.each do |e|
        if e == '('
          floor += 1
          position += 1
        else
          floor -= 1
          position += 1
        end
      end
    end

    puts"Floor: #{floor} and Position #{position}"

end




floor(floor_array)
